mapper.insert:
INSERT INTO txn_details(
) VALUES( #{txnId},#{fcTxnId},#{txnType},#{merchantTxnType},#{merchantTxnId}, #{settlementId},#{settlementDate},#{txnRef},#{txnRefType},#{merchantFee}, #{txnAmount},#{serviceTax},#{swachBharatCess},#{amountPayable},#{netDeduction}, #{merchantId},#{merchantName},#{customerName},#{btsTxnRef}, #{storeId},#{storeName},#{productId},#{terminalId},#{platform}, #{osVersion},#{custmerIP},#{location},#{shippingCity},#{source}, #{txnDate},now(3),now(3),#{userId},#{tsmTimeStamp},#{merchantTag},#{displayInfo}, #{txnMetaData},#{payableMetaData},#{disbursementEngineMetadata} )ON DUPLICATE KEY UPDATE
txn_ref = IF(tsm_time_stamp <= #{tsmTimeStamp}, #{txnRef}, txn_ref), txn_ref_type = IF(tsm_time_stamp <= #{tsmTimeStamp},#{txnRefType}, txn_ref_type), updated_on = IF(tsm_time_stamp <= #{tsmTimeStamp},now(3), updated_on), tsm_time_stamp = IF(tsm_time_stamp <= #{tsmTimeStamp},#{tsmTimeStamp}, tsm_time_stamp)

mapper.select:
SELECT txn_id
FROM txn_details
where bts_txn_ref = #{txnRef}
